<?php
// Login
if (isset($_POST['email']) && isset($_POST['password'])) {
  // Récupérer les données
  $email = $_POST['email'];
  $password = $_POST['password'];
  
  // Vérifier les données
  if (verify_email($email) && verify_password($password)) {
    // Connecter l'utilisateur
    connect_user($email, $password);
  } else {
    // Afficher un message d'erreur
    echo 'Email ou mot de passe incorrect';
  }
}

// Register
if (isset($_POST['name']) && isset($_POST['email']) && isset($_POST['password'])) {
  // Récupérer les données
  $name = $_POST['name'];
  $email = $_POST['email'];
  $password = $_POST['password'];
  
  // Vérifier les données
  if (verify_name($name) && verify_email($email) && verify_password($password)) {
    // Créer un compte
    create_account($name, $email, $password);
  } else {
    // Afficher un message d'erreur
    echo 'Les informations saisies sont incorrectes';
  }
}
